"use client";

import { useEffect, useRef, useState } from "react";
import Image from "next/image";
import { Popover } from "@headlessui/react";

export default function ImageRenderer(imglink: string | undefined) {
  const [src, setSrc] = useState(imglink);

  const buttonRef = useRef(null); // useRef<HTMLButtonElement>(null)
  const [openState, setOpenState] = useState(false);

  let timeout: number = 0; // NodeJS.Timeout
  const timeoutDuration = 400;

  const toggleMenu = (open: boolean) => {
    // log the current open state in React (toggle open state)
    setOpenState((openState) => !openState);
    // toggle the menu by clicking on buttonRef
    (buttonRef?.current as HTMLButtonElement | null)?.click(); // eslint-disable-line
  };

  // Open the menu after a delay of timeoutDuration
  const onHover = (open: boolean, action: string) => {
    // if the modal is currently closed, we need to open it
    // OR
    // if the modal is currently open, we need to close it
    if (
      (!open && !openState && action === "onMouseEnter") ||
      (open && openState && action === "onMouseLeave")
    ) {
      // clear the old timeout, if any
      clearTimeout(timeout);
      // open the modal after a timeout
      timeout = window.setTimeout(() => toggleMenu(open), timeoutDuration);
    }
    // else: don't click! 😁
  };

  const handleClick = (open: boolean) => {
    setOpenState(!open); // toggle open state in React state
    clearTimeout(timeout); // stop the hover timer if it's running
  };
  const handleClickOutside = (event: Event) => {
    if (
      buttonRef.current &&
      !(buttonRef.current as HTMLButtonElement | null)?.contains(
        event.currentTarget as Node
      )
    ) {
      event.stopPropagation();
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  });

  
  
  
  if (src) {
    const regexp = /^https?:\/\/[^?#\n]+/;
    const match = src.match(regexp);
    return (
      <>
        <Popover className="relative">
          {({ open }) => (
            <div
              onMouseEnter={() => onHover(open, "onMouseEnter")}
              onMouseLeave={() => onHover(open, "onMouseLeave")}
              className="cursor-pointer"
            >
              <Popover.Button ref={buttonRef}>
                <div
                  id="image-container"
                  className="relative h-16 w-16 cursor-pointer"
                >
                  <Image
                    alt="product image"
                    onError={() =>
                      setSrc(
                        "data:image/jpeg;base64,"
                      )
                    }
                    placeholder="data:image/jpeg;base64,"
                    objectFit="contain"
                    fill={true}
                    src={match ? match[0]:src}
                  />
                </div>
              </Popover.Button>
              <Popover.Panel className="absolute z-10 h-96 w-96">
                <Image
                  alt="product image"
                  onError={() =>
                    setSrc(
                      "data:image/jpeg;base64,"
                    )
                  }
                  placeholder="data:image/jpeg;base64,"
                  objectFit="contain"
                  fill={true}
                  src={match ? match[0]:src}
                />
              </Popover.Panel>
            </div>
          )}
        </Popover>
      </>
    );
  } else {
    return (
      <div className="relative h-16 w-16">
        <Image
          alt="product image"
          objectFit="contain"
          src={
            "data:image/jpeg;base64,"
          }
          fill={true}
        />
      </div>
    );
  }
}
